import React,{ useState,useEffect,useLayoutEffect} from 'react'
import TransactionRow from './TransactionRow';
import { Table,Input,Button } from 'semantic-ui-react';
import '../App.css' 

const ShowRequest=(props)=> {
  const[current,setCurrent]=useState(0) //dummy state
  const[reqNumber,setReq]=useState(null)

  const initialInfo=async()=>{

  let f=await props.contract.methods.getreqNo().call()
  const info = await Promise.all(
    Array(parseInt(f)).fill().map((element, index) => {
        return props.contract.methods.getRequeststatus(index).call();
      })
  )
   localStorage.setItem("info",JSON.stringify(info))



    }


    useEffect(()=>{

      initialInfo();
      handler();
      console.log("use effect used as layouteffect")
      
    },[])

  useEffect(()=>{


    console.log("inside useeffect")
    initialInfo();
    handler();
    console.log("useeffect used foe update")
    
  },[props.refresh])
  

const handler=()=>{
  setTimeout(()=>{
    setCurrent(!current)
  },1000)
}



const VoteHandler = async () => {

  await props.contract.methods.voteRequest(reqNumber).send({
    from: props.account[0]
  });
  handler();

};




const PaymentHandler = async () => {

  await props.contract.methods.settleRequest(reqNumber).send({
    from: props.account[0]


  });

};



  return (
    <div>

      {console.log("render")}
      {console.log(current)}
      <h2>Request Table:</h2>
  <TransactionRow
         account={props.account}
         contract={props.contract}
         web3={props.web3}
         />  

    
<Table.Footer fullWidth>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell colSpan='7'>
      <Input position='right' placeholder='Request Number' onChange={e=>setReq(e.target.value)} />    
        <p></p>
        <Button size='small' color="green" onClick={VoteHandler}>Vote</Button>
        <Button
          floated='left'
          icon
          labelPosition='right'
          primary
          size='small'
          onClick={PaymentHandler}
        >
         
        Make Payment
        </Button >
      </Table.HeaderCell>
    </Table.Row>
  </Table.Footer>
     

        
 </div> 
  )
}
export default ShowRequest
