import React,{Component} from "react";
import {Table,Button,Input} from 'semantic-ui-react'



class TransactionRow extends Component {
  
  constructor(props) {
    super(props);
    this.state = { 
      reqNumber:null,
      dummyValue:"0"
     };
     const datas=JSON.parse(localStorage.getItem("info" || "[]"))
  }

   VoteHandler = async () => {

    await this.props.contract.methods.voteRequest(this.state.reqNumber).send({
      from: this.props.account[0]

    });
    this.setState({dummyValue: this.state.dummyValue+1})
  };


  
PaymentHandler = async () => {

    await this.props.contract.methods.settleRequest(this.state.reqNumber).send({
      from: this.props.account[0]


    });

  };


  rerender=()=>{
    this.forceUpdate()
  }
  render() {
    const datas=JSON.parse(localStorage.getItem("info" || "[]"))
    return (
    
            <div className='ui conatiner '>
        {console.table(datas[0])}
    <Table compact celled definition >
  <Table.Header>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell>Request_NO</Table.HeaderCell>
      <Table.HeaderCell>Creator</Table.HeaderCell>
      <Table.HeaderCell>Description:</Table.HeaderCell>
      <Table.HeaderCell>Amount:</Table.HeaderCell>
      <Table.HeaderCell>Recipient:</Table.HeaderCell>
      <Table.HeaderCell>Voters/Donors</Table.HeaderCell>
      <Table.HeaderCell>Payment Completed</Table.HeaderCell>
     
    </Table.Row>
  </Table.Header>


  <Table.Body>


{
  datas.map((e,index)=>(
    
      <Table.Row key={index.toString()}>
        <Table.Cell>
        </Table.Cell>
        <Table.Cell>{index}</Table.Cell>
        <Table.Cell>{e[6]}</Table.Cell>
        <Table.Cell>{e[5]}</Table.Cell>
        <Table.Cell>{this.props.web3.utils.fromWei(e[1])}</Table.Cell>
        <Table.Cell>{e[2]}</Table.Cell>
        <Table.Cell>{e[3]}/{e[4]}</Table.Cell>
      <Table.Cell>{e[0]}</Table.Cell>
      </Table.Row>
    ))
    
}
</Table.Body>
  




    <Table.Footer fullWidth>
    <Table.Row>
      <Table.HeaderCell />
      <Table.HeaderCell colSpan='7'>
      <Input position='right' placeholder='Request Number' onChange={e => this.setState({ reqNumber: e.target.value })} />    
        <p></p>
        <Button size='small' color="green" onClick={()=>{this.VoteHandler();this.rerender()}}>Vote</Button>
        <Button
          floated='left'
          icon
          labelPosition='right'
          primary
          size='small'
          onClick={this.PaymentHandler}
        >
         
        Make Payment
        </Button >
      </Table.HeaderCell>
    </Table.Row>
  </Table.Footer>

  </Table>

    </div>
      
    )
  };
}

export default TransactionRow;
