import React, { Component } from 'react';
import {
  Button,
  Checkbox,
  Grid,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar,
} from 'semantic-ui-react'
import { BrowserRouter as Router, NavLink, Switch, Route } from 'react-router-dom';
import Guideline from '../dashComponents/Guideline';
import Tender from '../dashComponents/Tender';
import '../dashComponents/sidebar.css';
import aboutUS from '../dashComponents/aboutUS';
import RequestLog from '../dashComponents/RequestLog';
import TenderLogs from '../dashComponents/TenderLogs';
import '../App.css'


let array = new Array(1).fill().map(() => new Array(8).fill('0')) //this is dummy array of objects used for local storage 
localStorage.setItem("info", JSON.stringify(array))


const HorizontalSidebar = ({ animation, direction, visible }) => (
  <Sidebar
    as={Segment}
    animation={animation}
    direction={direction}
    visible={visible}
  >
    <Grid textAlign='center'>
      <Grid.Row columns={1}>
        <Grid.Column>
          <Header as='h3'>New Content Awaits</Header>
        </Grid.Column>
      </Grid.Row>
      <Grid.Row columns={3}>
        <Grid.Column>
          <Image src='/images/wireframe/media-paragraph.png' />
        </Grid.Column>
        <Grid.Column>
          <Image src='/images/wireframe/media-paragraph.png' />
        </Grid.Column>
        <Grid.Column>
          <Image src='/images/wireframe/media-paragraph.png' />
        </Grid.Column>
      </Grid.Row>
    </Grid>
  </Sidebar>
)

const VerticalSidebar = ({ animation, direction, visible }) => (
  <Sidebar
    as={Menu}
    animation={animation}
    direction={direction}
    icon='labeled'
    inverted
    vertical
    visible={visible}
    width='thin'
  >
    <Menu.Item as='a'>
      <Icon name='home' />
      Home
    </Menu.Item>
    <Menu.Item as='a'>
      <Icon name='gamepad' />
      Games
    </Menu.Item>
    <Menu.Item as='a'>
      <Icon name='camera' />
      Channels
    </Menu.Item>
  </Sidebar>
)

function exampleReducer(state, action) {
  switch (action.type) {
    case 'CHANGE_ANIMATION':
      return { ...state, animation: action.animation, visible: !state.visible }
    case 'CHANGE_DIMMED':
      return { ...state, dimmed: action.dimmed }
    case 'CHANGE_DIRECTION':
      return { ...state, direction: action.direction, visible: false }
    default:
      throw new Error()
  }
}

function Dashboard() {
  const [state, dispatch] = React.useReducer(exampleReducer, {
    animation: 'overlay',
    direction: 'left',
    dimmed: false,
    visible: false,
  })

  const { animation, dimmed, direction, visible } = state
  const vertical = direction === 'bottom' || direction === 'top'

  return (
    <div>
      <Checkbox
        checked={dimmed}
        label='Dim Page'
        onChange={(e, { checked }) =>
          dispatch({ type: 'CHANGE_DIMMED', dimmed: checked })
        }
        toggle
      />

      <Header as='h5'>Direction</Header>
      <Button.Group>
        <Button
          active={direction === 'left'}
          onClick={() =>
            dispatch({ type: 'CHANGE_DIRECTION', direction: 'left' })
          }
        >
          Left
        </Button>
        <Button
          active={direction === 'right'}
          onClick={() =>
            dispatch({ type: 'CHANGE_DIRECTION', direction: 'right' })
          }
        >
          Right
        </Button>
        <Button
          active={direction === 'top'}
          onClick={() =>
            dispatch({ type: 'CHANGE_DIRECTION', direction: 'top' })
          }
        >
          Top
        </Button>
        <Button
          active={direction === 'bottom'}
          onClick={() =>
            dispatch({ type: 'CHANGE_DIRECTION', direction: 'bottom' })
          }
        >
          Bottom
        </Button>
      </Button.Group>

      <Header as='h5'>All Direction Animations</Header>
      <Button
        onClick={() =>
          dispatch({ type: 'CHANGE_ANIMATION', animation: 'overlay' })
        }
      >
        Overlay
      </Button>
      <Button
        onClick={() =>
          dispatch({ type: 'CHANGE_ANIMATION', animation: 'push' })
        }
      >
        Push
      </Button>
      <Button
        onClick={() =>
          dispatch({ type: 'CHANGE_ANIMATION', animation: 'scale down' })
        }
      >
        Scale Down
      </Button>

      <Header as='h5'>Vertical-Only Animations</Header>
      <Button
        disabled={vertical}
        onClick={() =>
          dispatch({ type: 'CHANGE_ANIMATION', animation: 'uncover' })
        }
      >
        Uncover
      </Button>
      <Button
        disabled={vertical}
        onClick={() =>
          dispatch({ type: 'CHANGE_ANIMATION', animation: 'slide along' })
        }
      >
        Slide Along
      </Button>
      <Button
        disabled={vertical}
        onClick={() =>
          dispatch({ type: 'CHANGE_ANIMATION', animation: 'slide out' })
        }
      >
        Slide Out
      </Button>

      <Sidebar.Pushable as={Segment} style={{ overflow: 'hidden' }}>
        {vertical && (
          <HorizontalSidebar
            animation={animation}
            direction={direction}
            visible={visible}
          />
        )}
        {!vertical && (
          <VerticalSidebar
            animation={animation}
            direction={direction}
            visible={visible}
          />
        )}

        <Sidebar.Pusher dimmed={dimmed && visible}>
          <Segment basic>
            <Header as='h3'>Application Content</Header>
            <Image src='/images/wireframe/paragraph.png' />
          </Segment>
        </Sidebar.Pusher>
      </Sidebar.Pushable>
    </div>
  )
}

export default Dashboard
