import React, { Component } from 'react';

import {
  Checkbox,
  Grid,
  Header,
  Icon,
  Image,
  Menu,
  Segment,
  Sidebar,
} from 'semantic-ui-react'
import { BrowserRouter as Router, NavLink, Switch, Route } from 'react-router-dom';
import Guideline from '../dashComponents/Guideline';
import Tender from '../dashComponents/Tender';
import '../dashComponents/sidebar.css';
import aboutUS from '../dashComponents/aboutUS';
import RequestLog from '../dashComponents/RequestLog';
import TenderLogs from '../dashComponents/TenderLogs';
import '../App.css'


let array = new Array(1).fill().map(() => new Array(8).fill('0')) //this is dummy array of objects used for local storage 
localStorage.setItem("info", JSON.stringify(array))

const Dashboard = () => {
  const [visible, setVisible] = React.useState(false)

  return (
    <Grid columns={1}>
      <Grid.Column>
        <Checkbox
          checked={visible}
          label={{ children: <code>visible</code> }}
          onChange={(e, data) => setVisible(data.checked)}
        />
      </Grid.Column>

      <Grid.Column>
        <Sidebar.Pushable as={Segment}>
          <Sidebar
            as={Menu}
            animation='overlay'
            icon='labeled'
            inverted
            onHide={() => setVisible(false)}
            vertical
            visible={visible}
            width='thin'
          >
            <Menu.Item as='a'>
              <Icon name='home' />
              Home
            </Menu.Item>
            <Menu.Item as='a'>
              <Icon name='gamepad' />
              Games
            </Menu.Item>
            <Menu.Item as='a'>
              <Icon name='camera' />
              Channels
            </Menu.Item>
          </Sidebar>

          <Sidebar.Pusher dimmed={visible}>
            <Segment basic>
              <Header as='h3'>Application Content</Header>
              <Image src='/images/wireframe/paragraph.png' />
            </Segment>
          </Sidebar.Pusher>
        </Sidebar.Pushable>
      </Grid.Column>
    </Grid>
  )
}


export default Dashboard
